gsap.from("footer", {
    opacity: 0,
    y: 100,
    duration: 1
});

var snapEdges = gsap.utils.snap({vaules:[0, window,innerWidth], radius: 300}),
widthToProgress = gsap.utils.interpolate("#C00", "#00C");

function onMove(mouseX) {
    var value = interpColor(widthToProgress(snapEdges(mouseX)));
    document.body.style.h1 = value;
    display(value);
}